update TB_TEXT
set TEXT = #{text}
    , STATUS = #{status}
	, COMMENT = #{comment}
	, MODIFIED_TIME = now()
	, MODIFIED_IP = #{modifiedIp}
where CODE = #{code}
	and LOCALE = #{locale}

